INSERT INTO `js_content` VALUES ('55', 'Browser 对象参考手册', '20', '\r\n<p>点击以下链接，可以获得以下对象的更多信息，包括它们的集合、属性、方法以及事件。<em>其中包含大量实例！</em></p>\r\n\r\n<table class=\"dataintable\">\r\n  <tr>\r\n    <th style=\"width:25%\">对象</th>\r\n    <th>描述</th>\r\n  </tr>\r\n  <tr>\r\n    <td><a href=\"dom_obj_window.asp.htm\" tppabs=\"http://www.w3school.com.cn/htmldom/dom_obj_window.asp\" title=\"HTML DOM Window 对象\">Window</a></td>\r\n    <td>JavaScript 层级中的顶层对象，表示浏览器窗口。</td>\r\n  </tr>\r\n	<tr>\r\n    <td><a href=\"dom_obj_navigator.asp.htm\" tppabs=\"http://www.w3school.com.cn/htmldom/dom_obj_navigator.asp\" title=\"HTML DOM Navigator 对象\">Navigator</a></td>\r\n    <td>包含客户端浏览器的信息。</td>\r\n  </tr>\r\n	<tr>\r\n    <td><a href=\"dom_obj_screen.asp.htm\" tppabs=\"http://www.w3school.com.cn/htmldom/dom_obj_screen.asp\" title=\"HTML DOM Screen 对象\">Screen</a></td>\r\n    <td>包含客户端显示屏的信息。</td>\r\n  </tr>\r\n  <tr>\r\n    <td><a href=\"dom_obj_history.asp.htm\" tppabs=\"http://www.w3school.com.cn/htmldom/dom_obj_history.asp\" title=\"HTML DOM History 对象\">History</a></td>\r\n    <td>包含了浏览器窗口访问过的 URL。</td>\r\n  </tr>\r\n  <tr>\r\n    <td><a href=\"dom_obj_location.asp.htm\" tppabs=\"http://www.w3school.com.cn/htmldom/dom_obj_location.asp\" title=\"HTML DOM Location 对象\">Location</a></td>\r\n    <td>包含了当前 URL 的信息。</td>\r\n  </tr>\r\n  </table>', null, '0', null, null);
